/*variables*/
:root{
  --pageBackgroundColor: #170e14;
  --footerBackgroundColor: #170e14;
  --textColor: #d9d1d6;
  --contrastColor: #D025F2;
  --textFooterColor: #f6f6fe;
  --cardBackground: #FCEAFA;
}

/*reset*/
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  text-decoration: none;
}
li{
  list-style-type: none;
  margin: 0 0.5rem;
}

/*Estilos base*/
html {
  scroll-behavior: smooth;
}

body{
  background: var(--pageBackgroundColor);
  font-family: 'Trispace', sans-serif;
  text-align: center;
  color: var(--textColor);
}

h1{
  font-size: calc(3rem + 1vw);
}

h2{
  font-size: calc(1.3em + 0.6vw);
}

p{
  font-size: calc(0.8em + 0.2vw);
  margin: calc(1em + 0.2vw) 0;
  line-height: 1.5rem;
  margin: 0;
}

a{
  color: var(--contrastColor);
}

/*Header*/
.nav-container {
  width: 100%;
  background-color: var(--footerBackgroundColor);
}

.navbar {
  display: grid;
  grid-template-columns: 0.2fr auto 1fr;
  align-items: center;
  height: 80px;
  width: 90%;
  max-width: 1720px;
  margin: 0 auto;
}

#navbar-logo{
  font-size: 2rem;
  min-width: 150px;
  text-align: left;
  margin-left: 3rem;
  color: var(--contrastColor);
  justify-self: start;
  cursor: pointer;
}

.nav-menu {
  display: grid;
  grid-template-columns: repeat(7, auto);
  list-style: none;
  text-align: center;
  width: 70%;
  justify-self: end;
}

.nav-links {
  color: var(--textFooterColor);
}

.nav-links:hover {
  background-color: var(--contrastColor);
  transition: all 0.2s ease-out;
}

.menu-toggle .bar {
  width: 25px;
  height: 3px;
  margin: 5px auto;
  transition: all 0.3s ease-in-out;
  background: var(--textFooterColor);
}

/*Footer*/
footer{
  background-color: var(--footerBackgroundColor);
}

footer li, footer a{
  font-family: 'Montserrat', sans-serif;
  color: var(--textFooterColor);
}

/* ------- */
main {
  display: grid;
  grid-template-columns: 1fr repeat(12, minmax(auto, 60px)) 1fr;
  grid-gap: 40px;
  padding: 3rem;
}

main p {
  margin-bottom: 2rem;
  font-size: 1.2rem;
}
/* ------- */
.main-info {
  grid-column: 2 / span 12;
  margin-bottom: 10vh;
}

.main-container {
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-items: center;
  justify-self: center;
  margin: 0;
  height: auto;
}

.main-content {
  justify-self: start;
  margin-left: 2rem;
  width: 60vw;
  text-align: left;
}

#main-img {
  height: 90%;
  width: 90%;
}
/* ------- */
.main-categories {
  grid-column: 2 / span 12;
  margin-bottom: 15vh;
}

.main-categories h2 {
  margin-bottom: 3rem;
}

.categories-cards {
  display: grid;
  grid-template-columns:  repeat(12, minmax(auto, 60px)) ;
  grid-gap: 40px;
  justify-content: center;
}

.category-card { 
  display: flex;
  grid-column-end:span 4;
  width: 100%;
    height: 100%;

  /* border: 3px dotted var(--contrastColor); */

  border-radius: 10px;
  /* min-width: 350px; */
  border: 1px solid var(--cardBackground);
  /* box-shadow: 
              5px 5px 5px rgba(0,0,0,0.3),
              10px 10px 10px rgba(255,255,255,0.1); */

}

.category-card a {
  width: 100%;
}

.category-card:hover {
  transform:translateY(-10px);
  border: 1px solid var(--contrastColor);
  background-color: var(--cardBackground);
  color: var(--pageBackgroundColor);
  transition: 0.5s;
  box-shadow: 0 0.8px 0.2px rgba(239, 62, 220, 0.034),
  0 4.7px 3.3px rgba(239, 62, 220, 0.048),
  0 10.5px 8px rgba(239, 62, 220, 0.06),
  0 15.3px 10.9px rgba(239, 62, 220, 0.072),
  0 34.8px 26.4px rgba(239, 62, 220, 0.086);
}

.category-card_container {
  display: block;
  padding: 3rem 0;
  position: relative;
  top: 50%;
  transform: translateY(-50%);
  max-width: 100%;
}

.category-card_content h3{
  color: var(--contrastColor);
  font-weight: normal;
  margin-top: 2.25rem;
}

.category-img {
  height: 60%;
  width: 60%;
}
/* ------- */
.all-books {
  grid-column: 2 / span 12;
}

.all-books h2 {
    margin-bottom: 3rem;
}

.cards {
  grid-column: 2 / span 12;
  display: grid;
  grid-template-columns: repeat(12, minmax(auto, 60px));
  grid-gap: 40px;
}

.card {
  grid-column-end: span 3;
  display: flex;
  flex-direction: column;
  background-color: var(--pageBackgroundColor);
  cursor: pointer;
  transition: all 0.3s ease 0s;
}

.card_link {
  display: block; 
  width: 100%; 
  height:100%;
  grid-column-end: span 3;
}

.card:hover {
  transform: translateY(-10px);
  box-shadow: 0 0.8px 0.2px rgba(239, 62, 220, 0.034),
  0 4.7px 3.3px rgba(239, 62, 220, 0.048),
  0 10.5px 8px rgba(239, 62, 220, 0.06),
  0 15.3px 10.9px rgba(239, 62, 220, 0.072),
  0 34.8px 26.4px rgba(239, 62, 220, 0.086);
}

