.detail-intro {
  grid-column: 2 / span 12;
  text-align: left;
}

.detail-intro h1 {
  margin-top: 2rem;
  margin-bottom: 1rem;
}

.detail-intro h3 {
  font-weight: lighter;
  color: var(--contrastColor);
}

.detail-book {
  grid-column: 2 / span 12;
  display: flex;
}

.detail-description p{
  margin-left: 2.5rem;
  text-align: left;
}

.detail-author {
  grid-column: 2 / span 12;
}

.breadcrumb {
  display: flex;
  text-align: left;
  padding: 0;
}

.breadcrumb li {
  margin: 0;
}

.breadcrumb li:first-child::after {
  content: ">";
  margin: 0 1rem;
}

.tag {
  min-width: 100px;
  background-color: var(--contrastColor);
  padding: 0.5rem 1rem;
  margin: 2rem 0;
}

/* Responsive iframe style */
.embed-video {
  overflow: hidden;
  padding-top: 56.25%;
  position: relative;
}

.embed-video iframe {
  position: absolute;
  top: 0;
  left: 0;
  border: 0;
  width: 100%;
  height: 100%;
}

@media only screen and (max-width: 1150px) {
  .card {
    grid-column-end: span 6;
  }

    /* Navbar styles */
    .nav-container {
      position: relative;
    }

    .nav-menu {
      display: grid;
      grid-template-columns: auto;
      background: var(--contrastColor);
      margin: 0;
      width: 100%;
      position: absolute;
      top: 80px;
      left: 100%;
      opacity: 0;
      z-index: 1;
      transition: all 0.5s ease;
    }

    .nav-menu.active {
      background: var(--footerBackgroundColor);
      left: 0;
      opacity: 1;
      transition: all 0.5s ease;
    }

    .nav-links {
      text-align: center;
      line-height: 60px;
      width: 100%;
      display: table;
    }

    .nav-links:hover {
      background-color: var(--contrastColor);
      color: var(--textFooterColor);
      transition: all 0.4s ease-out;
    }

    .navbar {
      width: 100%;
    }

    #mobile-menu {
      position: absolute;
      top: 20%;
      right: 5%;
      transform: translate(15%, 20%);
    }

    .menu-toggle .bar {
      display: block;
      cursor: pointer;
    }

    .menu-toggle:hover {
      cursor: pointer;
    }

    #mobile-menu.is-active .bar:nth-child(2){
      opacity: 0;
    }

    #mobile-menu.is-active .bar:nth-child(1){
      transform: translateY(8px) rotate(45deg);
    }

    #mobile-menu.is-active .bar:nth-child(3){
      transform: translateY(-8px) rotate(-45deg);
    }

    /* Categories cards style */
    .category-card {
      grid-column-end: span 6;
    }
}

@media only screen and (max-width: 700px) {

  main {
    grid-gap: 10px;
  }

  .main-content {
    grid-column: 1 / span 2;
  }

  .main-img-container {
    display: none;
  }

.categories-cards {
  grid-gap: 20px;
}

.category-card {
  grid-column-end: span 12;
  padding: 2rem 0;
}

.cards {
  grid-column: 2 / span 6;
  grid-template-columns: repeat(6, 1fr);
  grid-gap: 20px;
}

.card_link {
  grid-column-end: span 3;
}

@media only screen and (max-width: 500px) {
  .card_link {
    grid-column-end: span 6;
  }


  }
}
